var masterBathroomHumidity = flow.get("masterBathroomHumidity") ?? 0
var guestBathroomHumidity = flow.get("guestBathroomHumidity") ?? 0
var start_state = flow.get("state") ?? "OFF"
var state = start_state

if (msg.topic === "zigbee2mqtt/humidity master bathroom") {
    masterBathroomHumidity = msg.payload.humidity
    flow.set("masterBathroomHumidity", msg.payload.humidity)
}

if (msg.topic === "zigbee2mqtt/humidity guest bathroom") {
    guestBathroomHumidity = msg.payload.humidity
    flow.set("guestBathroomHumidity", msg.payload.humidity)
}

var humidity = Math.max(masterBathroomHumidity, guestBathroomHumidity)

if (humidity > 80)
    state = "ON"

if (humidity < 70)
    state = "OFF"

if (start_state != state)
{
    flow.set("state", state)

    return { payload: { state_bottom: state } }
}

return { payload: {  } }
