var sensor = flow.get("sensor");

if (sensor.masterBathroom.humidity >= 80 || sensor.guestBathroom.humidity >= 80)
{
    msg.payload = {state_bottom: "ON"};
    return msg;
}
else if (sensor.masterBathroom.humidity <= 70 && sensor.guestBathroom.humidity <= 70)
{
    msg.payload = {state_bottom: "OFF"};
    return msg;
}

var prowermonitor = flow.get("prowermonitor");

if (msg.payload.power != prowermonitor.power)
{
    flow.set("prowermonitor", msg.payload);
    if (msg.payload.power === "ON")
    {
        msg.payload = { "state": "OFF" };
        return msg;
    }
